import { motion } from "framer-motion";
import { useState } from "react";

// Sample project data - replace with your actual projects
const projectData = [
  {
    id: 1,
    title: "Application BDE",
    description: "Application web pour la gestion des événements du Bureau Des Étudiants",
    tags: ["React", "Node.js", "MongoDB"],
    image: "/assets/projects/bde-app.jpg",
    link: "#",
    github: "https://github.com/yourusername/bde-app"
  },
  {
    id: 2,
    title: "Application de Sport",
    description: "Une application mobile pour suivre ses performances sportives",
    tags: ["React Native", "Firebase", "Redux"],
    image: "/assets/projects/sport-app.jpg",
    link: "#",
    github: "https://github.com/yourusername/sport-app"
  },
  {
    id: 3,
    title: "Jeu Psyché",
    description: "Jeu vidéo explorant des concepts psychologiques",
    tags: ["Unity", "C#", "3D Modeling"],
    image: "/assets/projects/psyche-game.jpg",
    link: "#",
    github: "https://github.com/yourusername/psyche-game"
  }
];

export default function Projects() {
  return (
    <section id="projects" className="py-20 px-4 bg-white">
      <div className="max-w-6xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-3xl md:text-4xl font-bold text-center mb-3">Mes Projets</h2>
          <p className="text-gray-600 text-center mb-12 max-w-2xl mx-auto">
            Découvrez mes travaux récents à travers une sélection de projets développés dans différents contextes.
          </p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projectData.map((project, index) => (
            <ProjectCard key={project.id} project={project} index={index} />
          ))}
        </div>
      </div>
    </section>
  );
}

function ProjectCard({ project, index }) {
  const [isHovered, setIsHovered] = useState(false);
  
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.6, delay: index * 0.2 }}
      className="bg-white rounded-lg overflow-hidden shadow-lg hover:shadow-xl transition-shadow"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="h-48 overflow-hidden relative">
        <img 
          src={project.image} 
          alt={project.title}
          className="w-full h-full object-cover transform transition-transform duration-500 ease-in-out"
          style={{ transform: isHovered ? "scale(1.05)" : "scale(1)" }}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/400x200?text=${project.title}`;
            e.target.onerror = null;
          }}
        />
      </div>
      
      <div className="p-6">
        <h3 className="font-bold text-xl mb-2">{project.title}</h3>
        <p className="text-gray-600 mb-4">{project.description}</p>
        
        <div className="flex flex-wrap gap-2 mb-4">
          {project.tags.map((tag) => (
            <span key={tag} className="bg-gray-100 text-gray-800 text-xs px-3 py-1 rounded-full">
              {tag}
            </span>
          ))}
        </div>
        
        <div className="flex justify-between">
          <a 
            href={project.github} 
            target="_blank" 
            rel="noopener noreferrer"
            className="text-blue-600 hover:text-blue-800 transition-colors"
          >
            GitHub
          </a>
          <a 
            href={project.link} 
            target="_blank" 
            rel="noopener noreferrer"
            className="text-blue-600 hover:text-blue-800 transition-colors"
          >
            Voir le projet
          </a>
        </div>
      </div>
    </motion.div>
  );
}
